
def codon_locations(dna):
	last_start = [None] * (len(dna) - 2)
	last_stop  = [None] * (len(dna) - 2)
	next_start = [None] * (len(dna) - 2)
	next_stop  = [None] * (len(dna) - 2)

	lstarts = {0:None, 1:None, 2:None}
	lstops  = {0:None, 1:None, 2:None}
	nstarts = {0:None, 1:None, 2:None}
	nstops  = {0:None, 1:None, 2:None}
	for frame in [0,1,2]:
		nstarts[frame] = lambda : None
		nstarts[frame].next = None
		nstops[frame] = lambda : None
		nstops[frame].next = None
	for i,_ in enumerate(dna[:-2]):
		codon = dna[i:i+3]
		frame = i % 3
		next_start[i] = nstarts[frame]
		if codon in ['ATG','GTG','TTG']:
			obj = lambda : None
			obj.last = i
			lstarts[frame] = obj
			# next
			nstarts[frame].next = i
			obj = lambda : None
			obj.next = i
			nstarts[frame] = obj
		last_start[i] = lstarts[frame]
		# stops
		next_stop[i] = nstops[frame]
		if codon in ['TAG','TGA','TAA']:
			obj = lambda : None
			obj.last = i
			lstops[frame] = obj
			# next
			nstops[frame].next = i
			obj = lambda : None
			obj.next = i
			nstops[frame] = obj
		last_stop[i] = lstops[frame]




	return last_start, last_stop, next_start, next_stop


seq = ""

last_start,last_stop,next_start,next_stop = codon_locations(seq * 1000)



import sys
from types import ModuleType, FunctionType
from gc import get_referents

# Custom objects know their class.
# Function objects seem to know way too much, including modules.
# Exclude modules as well.
BLACKLIST = type, ModuleType, FunctionType


def getsize(obj):
    """sum size of object & members."""
    if isinstance(obj, BLACKLIST):
        raise TypeError('getsize() does not take argument of type: '+ str(type(obj)))
    seen_ids = set()
    size = 0
    objects = [obj]
    while objects:
        need_referents = []
        for obj in objects:
            if not isinstance(obj, BLACKLIST) and id(obj) not in seen_ids:
                seen_ids.add(id(obj))
                size += sys.getsizeof(obj)
                need_referents.append(obj)
        objects = get_referents(*need_referents)
    return size


